import React, { useState } from 'react';
import { Mic, Globe, ArrowRight, Target, Users, BookOpen, Shield } from 'lucide-react';

// Props explanation:
// onGetStarted: Function passed from App.js to handle navigation to onboarding
const LandingPage = ({ onGetStarted }) => {
  // State for managing the language modal (open/closed)
  const [showLanguageModal, setShowLanguageModal] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState('English');

  // Function to handle "Start Your Journey" button click
  const handleStartJourney = () => {
    // Call the navigation function passed from App.js
    if (onGetStarted) {
      onGetStarted();
    } else {
      console.error('onGetStarted prop not provided to LandingPage');
    }
  };

  // Function to handle language selection
  const handleLanguageSelect = (language) => {
    setSelectedLanguage(language);
    setShowLanguageModal(false);
    // You could add logic here to change the app language
  };

  // Array of supported languages with their native names
  const languages = [
    { code: 'en', name: 'English', native: 'English' },
    { code: 'hi', name: 'Hindi', native: 'हिंदी' },
    { code: 'bn', name: 'Bengali', native: 'বাংলা' },
    { code: 'te', name: 'Telugu', native: 'తెలుగు' },
    { code: 'mr', name: 'Marathi', native: 'मराठी' },
    { code: 'ta', name: 'Tamil', native: 'தமிழ்' },
    { code: 'gu', name: 'Gujarati', native: 'ગુજરાતી' },
    { code: 'ur', name: 'Urdu', native: 'اردو' },
    { code: 'kn', name: 'Kannada', native: 'ಕನ್ನಡ' },
    { code: 'ml', name: 'Malayalam', native: 'മലയാളം' },
    { code: 'pa', name: 'Punjabi', native: 'ਪੰਜਾਬੀ' },
    { code: 'as', name: 'Assamese', native: 'অসমীয়া' }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white">
      {/* Header */}
      <header className="flex justify-between items-center p-6 md:p-8">
        {/* Logo and Brand */}
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-gradient-to-r from-green-500 to-blue-500 rounded-lg flex items-center justify-center">
            <Target className="w-6 h-6 text-white" />
          </div>
          <div>
            <h1 className="text-xl font-bold">PragatiPath</h1>
            <p className="text-sm text-gray-400">PM Internship Scheme</p>
          </div>
        </div>

        {/* Language Selector */}
        <button
          onClick={() => setShowLanguageModal(true)}
          className="flex items-center space-x-2 bg-gray-800 hover:bg-gray-700 px-4 py-2 rounded-lg transition-colors"
        >
          <Globe className="w-4 h-4" />
          <span className="text-sm">{selectedLanguage}</span>
        </button>
      </header>

      {/* Main Content */}
      <main className="max-w-6xl mx-auto px-6 py-12">
        {/* Government Badge */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center bg-orange-600 text-white px-4 py-2 rounded-full text-sm font-medium">
            <Shield className="w-4 h-4 mr-2" />
            Government of India Initiative
          </div>
        </div>

        {/* Hero Section */}
        <div className="text-center mb-16">
          <h1 className="text-4xl md:text-6xl font-bold mb-6">
            <span className="bg-gradient-to-r from-green-400 via-blue-500 to-purple-600 bg-clip-text text-transparent">
              Your Career Journey
            </span>
            <br />
            <span className="text-white">Starts Here</span>
          </h1>
          
          <p className="text-xl text-gray-300 max-w-3xl mx-auto mb-8 leading-relaxed">
            AI-powered career guidance platform connecting rural and first-generation learners to 
            government internship opportunities. Equal opportunities for every Indian youth.
          </p>

          {/* Statistics */}
          <div className="grid grid-cols-3 gap-8 max-w-2xl mx-auto mb-12">
            <div className="text-center">
              <div className="text-3xl font-bold text-green-400 mb-2">10K+</div>
              <div className="text-gray-400">Internships</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-blue-400 mb-2">500+</div>
              <div className="text-gray-400">Mentors</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-purple-400 mb-2">12</div>
              <div className="text-gray-400">Languages</div>
            </div>
          </div>
        </div>

        {/* Voice Navigation Section */}
        <div className="text-center mb-12">
          <p className="text-lg text-gray-300 mb-6">Try voice navigation - speak in your language!</p>
          
          {/* Animated Microphone Button */}
          <button className="relative w-20 h-20 bg-gradient-to-r from-green-500 to-green-600 rounded-full flex items-center justify-center hover:scale-105 transition-transform shadow-lg">
            <Mic className="w-8 h-8 text-white" />
            {/* Pulsing animation rings */}
            <div className="absolute inset-0 rounded-full bg-green-500 opacity-20 animate-ping"></div>
            <div className="absolute inset-0 rounded-full bg-green-500 opacity-10 animate-ping" style={{animationDelay: '0.5s'}}></div>
          </button>
        </div>

        {/* Call to Action */}
        <div className="text-center mb-16">
          <button
            onClick={handleStartJourney} // This now calls the prop function
            className="group bg-gradient-to-r from-green-500 via-blue-500 to-purple-600 hover:from-green-600 hover:via-blue-600 hover:to-purple-700 text-white px-8 py-4 rounded-xl text-lg font-semibold transition-all duration-300 transform hover:scale-105 shadow-xl flex items-center mx-auto space-x-2"
          >
            <span>Start Your Journey</span>
            <ArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform" />
          </button>
        </div>

        {/* Features Grid */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-16">
          <div className="bg-gray-800 p-8 rounded-2xl text-center hover:bg-gray-750 transition-colors">
            <div className="w-16 h-16 bg-gradient-to-r from-green-500 to-green-600 rounded-full flex items-center justify-center mx-auto mb-6">
              <Target className="w-8 h-8 text-white" />
            </div>
            <h3 className="text-xl font-bold mb-4">Smart Matching</h3>
            <p className="text-gray-400">AI finds perfect internship matches based on your skills and location</p>
          </div>

          <div className="bg-gray-800 p-8 rounded-2xl text-center hover:bg-gray-750 transition-colors">
            <div className="w-16 h-16 bg-gradient-to-r from-blue-500 to-blue-600 rounded-full flex items-center justify-center mx-auto mb-6">
              <Users className="w-8 h-8 text-white" />
            </div>
            <h3 className="text-xl font-bold mb-4">Expert Mentorship</h3>
            <p className="text-gray-400">Connect with industry mentors for guidance and support</p>
          </div>

          <div className="bg-gray-800 p-8 rounded-2xl text-center hover:bg-gray-750 transition-colors">
            <div className="w-16 h-16 bg-gradient-to-r from-purple-500 to-purple-600 rounded-full flex items-center justify-center mx-auto mb-6">
              <BookOpen className="w-8 h-8 text-white" />
            </div>
            <h3 className="text-xl font-bold mb-4">Skill Development</h3>
            <p className="text-gray-400">Identify gaps and get personalized learning paths</p>
          </div>
        </div>
      </main>

      {/* Language Selection Modal */}
      {showLanguageModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-gray-800 rounded-2xl p-6 max-w-md w-full max-h-96 overflow-y-auto">
            <h3 className="text-xl font-bold mb-6 text-center">Choose Your Language</h3>
            <div className="grid grid-cols-2 gap-3">
              {languages.map((lang) => (
                <button
                  key={lang.code}
                  onClick={() => handleLanguageSelect(lang.name)}
                  className={`p-3 rounded-lg text-left transition-colors ${
                    selectedLanguage === lang.name 
                      ? 'bg-green-600 text-white' 
                      : 'bg-gray-700 hover:bg-gray-600 text-gray-300'
                  }`}
                >
                  <div className="font-medium">{lang.name}</div>
                  <div className="text-sm opacity-75">{lang.native}</div>
                </button>
              ))}
            </div>
            <button
              onClick={() => setShowLanguageModal(false)}
              className="w-full mt-6 bg-gray-700 hover:bg-gray-600 text-white py-3 rounded-lg transition-colors"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default LandingPage;
