import React, { useState } from 'react';
import './index.css'; // Make sure Tailwind CSS is imported

// Import your components
import LandingPage from './components/Landing/LandingPage';
import OnboardingQuest from './components/onboarding/OnboardingQuest';
import Dashboard from './components/dashboard/Dashboard';

function App() {
  const [currentPage, setCurrentPage] = useState('landing');
  const [userProfile, setUserProfile] = useState(null);

  const navigateToOnboarding = () => {
    setCurrentPage('onboarding');
    console.log('Navigating to onboarding...');
  };

  const navigateToDashboard = (profileData) => {
    setUserProfile(profileData);
    setCurrentPage('dashboard');
    console.log('Navigating to dashboard with profile:', profileData);
  };

  const navigateToLanding = () => {
    setCurrentPage('landing');
    setUserProfile(null);
  };

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'landing':
        return <LandingPage onGetStarted={navigateToOnboarding} />; // ← This prop is crucial!
      
      case 'onboarding':
        return (
          <OnboardingQuest 
            onComplete={navigateToDashboard} 
            onBack={navigateToLanding} 
          />
        );
      
      case 'dashboard':
        return (
          <Dashboard 
            userProfile={userProfile}
            onNavigate={(page) => setCurrentPage(page)}
          />
        );
      
      default:
        return <LandingPage onGetStarted={navigateToOnboarding} />;
    }
  };

  return (
    <div className="App">
      {renderCurrentPage()}
    </div>
  );
}

export default App;
