import React, { useState } from 'react';
import { 
  ChevronRight, 
  Mic, 
  MapPin, 
  GraduationCap, 
  Heart, 
  Zap, 
  Star,
  Play,
  Volume2,
  CheckCircle,
  ArrowLeft,
  Globe,
  Briefcase,
  Users,
  Award
} from 'lucide-react';

const PragatiPathOnboarding = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [isVoiceMode, setIsVoiceMode] = useState(false);
  const [userData, setUserData] = useState({
    name: '',
    education: '',
    location: '',
    interests: [],
    skills: [],
    language: 'hindi'
  });

  const onboardingSteps = [
    {
      id: 'welcome',
      title: 'Welcome to PragatiPath! ðŸŒŸ',
      subtitle: 'Your AI-powered career companion',
      type: 'welcome'
    },
    {
      id: 'language',
      title: 'Choose Your Language',
      subtitle: 'à¤…à¤ªà¤¨à¥€ à¤­à¤¾à¤·à¤¾ à¤šà¥à¤¨à¥‡à¤‚',
      type: 'language',
      options: [
        { value: 'hindi', label: 'à¤¹à¤¿à¤‚à¤¦à¥€', flag: 'ðŸ‡®ðŸ‡³' },
        { value: 'english', label: 'English', flag: 'ðŸ‡¬ðŸ‡§' },
        { value: 'marathi', label: 'à¤®à¤°à¤¾à¤ à¥€', flag: 'ðŸ‡®ðŸ‡³' },
        { value: 'gujarati', label: 'àª—à«àªœàª°àª¾àª¤à«€', flag: 'ðŸ‡®ðŸ‡³' }
      ]
    },
    {
      id: 'name',
      title: 'What should we call you?',
      subtitle: 'à¤†à¤ªà¤•à¤¾ à¤¨à¤¾à¤® à¤•à¥à¤¯à¤¾ à¤¹à¥ˆ?',
      type: 'input',
      inputType: 'text',
      placeholder: 'Enter your name / à¤…à¤ªà¤¨à¤¾ à¤¨à¤¾à¤® à¤²à¤¿à¤–à¥‡à¤‚'
    },
    {
      id: 'education',
      title: 'Your Education Level',
      subtitle: 'à¤†à¤ªà¤•à¥€ à¤¶à¤¿à¤•à¥à¤·à¤¾ à¤•à¤¾ à¤¸à¥à¤¤à¤°',
      type: 'select',
      options: [
        { value: '10th', label: '10th Pass', icon: 'ðŸ“š' },
        { value: '12th', label: '12th Pass', icon: 'ðŸŽ“' },
        { value: 'diploma', label: 'Diploma', icon: 'ðŸ“œ' },
        { value: 'graduate', label: 'Graduate', icon: 'ðŸŽ¯' },
        { value: 'postgraduate', label: 'Post Graduate', icon: 'ðŸ‘¨â€ðŸŽ“' }
      ]
    },
    {
      id: 'location',
      title: 'Where are you from?',
      subtitle: 'à¤†à¤ª à¤•à¤¹à¤¾à¤ à¤¸à¥‡ à¤¹à¥ˆà¤‚?',
      type: 'location',
      states: [
        'Madhya Pradesh', 'Maharashtra', 'Gujarat', 'Rajasthan', 
        'Uttar Pradesh', 'Bihar', 'Jharkhand', 'Chhattisgarh'
      ]
    },
    {
      id: 'interests',
      title: 'What interests you?',
      subtitle: 'à¤†à¤ªà¤•à¥€ à¤°à¥à¤šà¤¿ à¤•à¤¿à¤¸à¤®à¥‡à¤‚ à¤¹à¥ˆ?',
      type: 'multiselect',
      options: [
        { value: 'technology', label: 'Technology', icon: 'ðŸ’»' },
        { value: 'healthcare', label: 'Healthcare', icon: 'ðŸ¥' },
        { value: 'education', label: 'Education', icon: 'ðŸ“š' },
        { value: 'agriculture', label: 'Agriculture', icon: 'ðŸŒ¾' },
        { value: 'finance', label: 'Finance', icon: 'ðŸ’°' },
        { value: 'government', label: 'Government', icon: 'ðŸ›ï¸' },
        { value: 'environment', label: 'Environment', icon: 'ðŸŒ±' },
        { value: 'social', label: 'Social Work', icon: 'ðŸ¤' }
      ]
    },
    {
      id: 'skills',
      title: 'Your Skills',
      subtitle: 'à¤†à¤ªà¤•à¥‡ à¤•à¥Œà¤¶à¤²',
      type: 'skills',
      categories: [
        {
          name: 'Digital Skills',
          skills: ['Computer Basics', 'MS Office', 'Internet', 'Mobile Apps']
        },
        {
          name: 'Communication',
          skills: ['Hindi', 'English', 'Public Speaking', 'Writing']
        },
        {
          name: 'Other Skills',
          skills: ['Leadership', 'Teamwork', 'Problem Solving', 'Time Management']
        }
      ]
    },
    {
      id: 'complete',
      title: 'Quest Complete! ðŸŽ‰',
      subtitle: 'Ready to find your perfect internship?',
      type: 'completion'
    }
  ];

  const currentStepData = onboardingSteps[currentStep];

  const nextStep = () => {
    if (currentStep < onboardingSteps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSelection = (value) => {
    const stepId = currentStepData.id;
    if (stepId === 'interests' || stepId === 'skills') {
      setUserData(prev => ({
        ...prev,
        [stepId]: prev[stepId].includes(value) 
          ? prev[stepId].filter(item => item !== value)
          : [...prev[stepId], value]
      }));
    } else {
      setUserData(prev => ({ ...prev, [stepId]: value }));
    }
  };

  const toggleVoice = () => {
    setIsVoiceMode(!isVoiceMode);
  };

  const renderStep = () => {
    switch (currentStepData.type) {
      case 'welcome':
        return (
          <div className="text-center">
            <div className="w-24 h-24 bg-gradient-to-r from-green-400 to-blue-500 rounded-full mx-auto mb-6 flex items-center justify-center animate-bounce">
              <Star className="w-12 h-12 text-white" />
            </div>
            <h1 className="text-3xl font-bold mb-4 bg-gradient-to-r from-green-400 to-blue-500 bg-clip-text text-transparent">
              {currentStepData.title}
            </h1>
            <p className="text-lg text-gray-300 mb-8">{currentStepData.subtitle}</p>
            <div className="space-y-4 text-gray-400 text-sm">
              <div className="flex items-center justify-center gap-2">
                <Zap className="w-4 h-4 text-yellow-400" />
                <span>AI-powered internship matching</span>
              </div>
              <div className="flex items-center justify-center gap-2">
                <Users className="w-4 h-4 text-blue-400" />
                <span>Expert mentorship support</span>
              </div>
              <div className="flex items-center justify-center gap-2">
                <Award className="w-4 h-4 text-purple-400" />
                <span>Gamified learning journey</span>
              </div>
            </div>
          </div>
        );

      case 'language':
        return (
          <div className="text-center">
            <Globe className="w-16 h-16 text-blue-400 mx-auto mb-6" />
            <div className="grid grid-cols-2 gap-4">
              {currentStepData.options.map((option) => (
                <button
                  key={option.value}
                  onClick={() => handleSelection(option.value)}
                  className={`p-4 rounded-xl border-2 transition-all ${
                    userData.language === option.value
                      ? 'border-green-400 bg-green-400/20'
                      : 'border-gray-600 bg-gray-800/50'
                  }`}
                >
                  <div className="text-2xl mb-2">{option.flag}</div>
                  <div className="font-semibold">{option.label}</div>
                </button>
              ))}
            </div>
          </div>
        );

      case 'input':
        return (
          <div className="text-center">
            <GraduationCap className="w-16 h-16 text-green-400 mx-auto mb-6" />
            <div className="space-y-4">
              <div className="relative">
                <input
                  type={currentStepData.inputType}
                  placeholder={currentStepData.placeholder}
                  value={userData[currentStepData.id] || ''}
                  onChange={(e) => handleSelection(e.target.value)}
                  className="w-full p-4 bg-gray-800/50 border border-gray-600 rounded-xl text-white placeholder-gray-400 text-center text-lg"
                />
                <button
                  onClick={toggleVoice}
                  className={`absolute right-3 top-1/2 transform -translate-y-1/2 p-2 rounded-lg ${
                    isVoiceMode ? 'bg-red-500' : 'bg-blue-500'
                  }`}
                >
                  <Mic className="w-5 h-5 text-white" />
                </button>
              </div>
              {isVoiceMode && (
                <div className="flex items-center justify-center gap-2 text-sm text-blue-400">
                  <div className="w-2 h-2 bg-red-500 rounded-full animate-pulse"></div>
                  <span>Listening... Speak in Hindi or English</span>
                </div>
              )}
            </div>
          </div>
        );

      case 'select':
        return (
          <div className="text-center">
            <GraduationCap className="w-16 h-16 text-purple-400 mx-auto mb-6" />
            <div className="space-y-3">
              {currentStepData.options.map((option) => (
                <button
                  key={option.value}
                  onClick={() => handleSelection(option.value)}
                  className={`w-full p-4 rounded-xl border-2 transition-all flex items-center gap-3 ${
                    userData.education === option.value
                      ? 'border-purple-400 bg-purple-400/20'
                      : 'border-gray-600 bg-gray-800/50'
                  }`}
                >
                  <span className="text-2xl">{option.icon}</span>
                  <span className="font-semibold">{option.label}</span>
                  {userData.education === option.value && (
                    <CheckCircle className="w-5 h-5 text-purple-400 ml-auto" />
                  )}
                </button>
              ))}
            </div>
          </div>
        );

      case 'location':
        return (
          <div className="text-center">
            <MapPin className="w-16 h-16 text-orange-400 mx-auto mb-6" />
            <div className="grid grid-cols-2 gap-3">
              {currentStepData.states.map((state) => (
                <button
                  key={state}
                  onClick={() => handleSelection(state)}
                  className={`p-3 rounded-xl border transition-all text-sm ${
                    userData.location === state
                      ? 'border-orange-400 bg-orange-400/20 text-orange-400'
                      : 'border-gray-600 bg-gray-800/50 text-gray-300'
                  }`}
                >
                  {state}
                </button>
              ))}
            </div>
          </div>
        );

      case 'multiselect':
        return (
          <div className="text-center">
            <Heart className="w-16 h-16 text-pink-400 mx-auto mb-6" />
            <div className="grid grid-cols-2 gap-3">
              {currentStepData.options.map((option) => (
                <button
                  key={option.value}
                  onClick={() => handleSelection(option.value)}
                  className={`p-4 rounded-xl border transition-all ${
                    userData.interests.includes(option.value)
                      ? 'border-pink-400 bg-pink-400/20'
                      : 'border-gray-600 bg-gray-800/50'
                  }`}
                >
                  <div className="text-2xl mb-1">{option.icon}</div>
                  <div className="text-sm font-medium">{option.label}</div>
                  {userData.interests.includes(option.value) && (
                    <CheckCircle className="w-4 h-4 text-pink-400 mx-auto mt-2" />
                  )}
                </button>
              ))}
            </div>
            <p className="text-xs text-gray-400 mt-4">Select multiple options</p>
          </div>
        );

      case 'skills':
        return (
          <div className="text-center">
            <Briefcase className="w-16 h-16 text-blue-400 mx-auto mb-6" />
            <div className="space-y-6">
              {currentStepData.categories.map((category, catIndex) => (
                <div key={catIndex}>
                  <h4 className="text-sm font-semibold text-gray-300 mb-3">{category.name}</h4>
                  <div className="grid grid-cols-2 gap-2">
                    {category.skills.map((skill) => (
                      <button
                        key={skill}
                        onClick={() => handleSelection(skill)}
                        className={`p-2 rounded-lg border text-xs transition-all ${
                          userData.skills.includes(skill)
                            ? 'border-blue-400 bg-blue-400/20 text-blue-400'
                            : 'border-gray-600 bg-gray-800/50 text-gray-300'
                        }`}
                      >
                        {skill}
                      </button>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        );

      case 'completion':
        return (
          <div className="text-center">
            <div className="w-24 h-24 bg-gradient-to-r from-green-400 to-blue-500 rounded-full mx-auto mb-6 flex items-center justify-center">
              <CheckCircle className="w-12 h-12 text-white" />
            </div>
            <h1 className="text-3xl font-bold mb-4">{currentStepData.title}</h1>
            <p className="text-lg text-gray-300 mb-8">{currentStepData.subtitle}</p>
            
            <div className="bg-gray-800/50 rounded-xl p-6 mb-8">
              <h3 className="text-lg font-semibold mb-4">Your Profile Summary</h3>
              <div className="space-y-2 text-sm text-gray-300">
                <p><span className="text-green-400">Name:</span> {userData.name}</p>
                <p><span className="text-purple-400">Education:</span> {userData.education}</p>
                <p><span className="text-orange-400">Location:</span> {userData.location}</p>
                <p><span className="text-pink-400">Interests:</span> {userData.interests.length} selected</p>
                <p><span className="text-blue-400">Skills:</span> {userData.skills.length} selected</p>
              </div>
            </div>

            <div className="space-y-4">
              <div className="flex items-center justify-center gap-2 text-green-400">
                <Zap className="w-5 h-5" />
                <span>AI is analyzing your profile...</span>
              </div>
              <div className="w-full bg-gray-700 rounded-full h-2">
                <div className="bg-gradient-to-r from-green-400 to-blue-500 h-2 rounded-full animate-pulse" style={{width: '85%'}}></div>
              </div>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white">
      {/* Header */}
      <div className="flex items-center justify-between p-4">
        <div className="flex items-center gap-3">
          {currentStep > 0 && (
            <button onClick={prevStep} className="p-2 hover:bg-gray-700 rounded-lg">
              <ArrowLeft className="w-5 h-5" />
            </button>
          )}
          <div className="w-8 h-8 bg-gradient-to-r from-green-400 to-blue-500 rounded-lg flex items-center justify-center">
            <Star className="w-5 h-5 text-white" />
          </div>
          <h1 className="text-xl font-bold">PragatiPath</h1>
        </div>
        
        <div className="flex items-center gap-2">
          <span className="text-sm text-gray-400">{currentStep + 1}/{onboardingSteps.length}</span>
          <div className="w-16 bg-gray-700 rounded-full h-1">
            <div 
              className="bg-gradient-to-r from-green-400 to-blue-500 h-1 rounded-full transition-all duration-300"
              style={{ width: `${((currentStep + 1) / onboardingSteps.length) * 100}%` }}
            ></div>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="px-4 py-8">
        <div className="max-w-md mx-auto">
          <div className="text-center mb-8">
            <h2 className="text-2xl font-bold mb-2">{currentStepData.title}</h2>
            {currentStepData.subtitle && (
              <p className="text-gray-400">{currentStepData.subtitle}</p>
            )}
          </div>

          <div className="mb-8">
            {renderStep()}
          </div>

          {/* Voice Helper */}
          {currentStepData.type !== 'welcome' && currentStepData.type !== 'completion' && (
            <div className="text-center mb-6">
              <button
                onClick={toggleVoice}
                className="flex items-center gap-2 mx-auto px-4 py-2 bg-blue-600/20 border border-blue-500/30 rounded-full text-blue-400 text-sm"
              >
                <Mic className="w-4 h-4" />
                <span>Voice Help Available</span>
                <Volume2 className="w-4 h-4" />
              </button>
            </div>
          )}

          {/* Navigation */}
          <div className="flex gap-4">
            {currentStep === onboardingSteps.length - 1 ? (
              <button 
                className="flex-1 bg-gradient-to-r from-green-500 to-blue-500 text-white py-4 rounded-xl font-semibold flex items-center justify-center gap-2"
              >
                <Play className="w-5 h-5" />
                Start Your Journey
              </button>
            ) : (
              <button 
                onClick={nextStep}
                className="flex-1 bg-gradient-to-r from-green-500 to-blue-500 text-white py-4 rounded-xl font-semibold flex items-center justify-center gap-2"
              >
                Continue
                <ChevronRight className="w-5 h-5" />
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PragatiPathOnboarding;
