import React, { useState } from 'react';
import './index.css'; // Make sure Tailwind CSS is imported

// Import your components
import LandingPage from './components/Landing/LandingPage';
import OnboardingQuest from './components/onboarding/OnboardingQuest';

function App() {
  // App-level state to track which page to show
  const [currentPage, setCurrentPage] = useState('landing'); // 'landing', 'onboarding', 'dashboard'
  const [userProfile, setUserProfile] = useState(null); // Store user data

  // Navigation functions
  const navigateToOnboarding = () => {
    setCurrentPage('onboarding');
    console.log('Navigating to onboarding...');
  };

  const navigateToDashboard = (profileData) => {
    setUserProfile(profileData);
    setCurrentPage('dashboard');
    console.log('Navigating to dashboard with profile:', profileData);
  };

  const navigateToLanding = () => {
    setCurrentPage('landing');
    setUserProfile(null);
  };

  // Render different pages based on currentPage state
  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'landing':
        return <LandingPage onGetStarted={navigateToOnboarding} />;
      
      case 'onboarding':
        return <OnboardingQuest onComplete={navigateToDashboard} onBack={navigateToLanding} />;
      
      case 'dashboard':
        return (
          <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center">
            <div className="text-center">
              <h1 className="text-4xl font-bold mb-4">ðŸŽ‰ Welcome to your Dashboard!</h1>
              <p className="text-gray-300 mb-6">This will be your personalized internship recommendations</p>
              <p className="text-green-400">User: {userProfile?.name}</p>
              <p className="text-blue-400">Location: {userProfile?.location}</p>
              <button 
                onClick={navigateToLanding}
                className="mt-6 px-6 py-3 bg-green-500 hover:bg-green-600 rounded-lg font-semibold transition-colors"
              >
                Start Over
              </button>
            </div>
          </div>
        );
      
      default:
        return <LandingPage onGetStarted={navigateToOnboarding} />;
    }
  };

  return (
    <div className="App">
      {renderCurrentPage()}
    </div>
  );
}

export default App;
